import { useState } from "react";
import { useNavigate } from "react-router-dom";

import "./Stage2.css";
const Stage2 = () => {
  const navigate = useNavigate();
  const [answer, setAnswer] = useState("");
  const [hint2, setHint2] = useState(false);
  const [wrong, setWrong] = useState(false);
  const [viewbutton, setViewbutton] = useState(false);

  const handleCheck2 = () => {
    if (answer === "elitmus") {
      navigate("/pirate/stage2");
    } else {
      setWrong(true);
    }
  };

  const handleHint2 = () => {
    setHint2(true);
  };

  setTimeout(() => {
    setViewbutton(true);
  }, 1000);

  return (
    <div id="stage2Div">
      <div id="stage2Text">
        <p>Nice! Lets take it a step ahead.</p>
        <br />
        <h1>Decode the images.</h1>

        {/* <br /> */}
        <p>find the word to get your next clue.</p>
        <br />
      </div>

      <div id="stage2Div2">
        <div id="images3">
          <img className="stage2img" src="/images/plus.png" />
          <img className="stage2img" src="/images/tile.jpg" />
          <img className="stage2img" src="/images/arr.png" />
        </div>

        <div id="answerDiv">
          <input
            className="answerInput"
            type="text"
            onChange={(event) => setAnswer(event.target.value)}
          ></input>
          <button onClick={handleCheck2} className="piratebtn default">
            Check
          </button>
          {wrong && <p id="wrongAnswer">Oops !! wrong answer.</p>}
        </div>
      </div>

      <div id="sayingDiv">
        <span>
          <img
            className="move"
            id="jackStage1Image"
            src="/images/pic2-opp.webp"
          />
        </span>

        {hint2 && (
          <div className="move" id="thinkDiv">
            <p id="dialogue">
              Sounds like something
              <br /> valuable
            </p>
          </div>
        )}
        {viewbutton && (
          <button id="help" onClick={handleHint2} className="piratebtn info">
            Help me Jack
          </button>
        )}
      </div>
    </div>
  );
};

export default Stage2;
