import "./PirateLogin.css";
import { useState } from "react";
const [userId, setUserId] = useState("");
const [password, setPassword] = useState("");

const PirateLogin = () => {
  const users = [{ userId: "bharadwaj", password: "1234567" }];

  const handleLogin = () => {
    const userFound = users.filter((item) => item.userId === userId);
    if (userFound) {
      if (userFound.password === password) {
        Navigate("/clue1");
      }
    } else {
      Navigate("/error");
    }
  };
  return (
    <div id="pirateHome">
      <div id="pirateDiv">
        Enter userId :{" "}
        <input
          onChange={(event) => setUserId(event.target.value)}
          type="text"
        />
        <br />
        Enter Password :{" "}
        <input
          onChange={(event) => setUserId(event.target.value)}
          type="password"
        />
        <br />
        New User ? <br />
        <button onClick={handleLogin}>Login</button>
        <a href="/piratesignup"> SignUp</a>
      </div>
    </div>
  );
};

export default PirateLogin;
