import "./PirateLogin.css";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const PirateLogin = (props) => {
  const navigate = useNavigate();
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");

  const users = props.users;

  const handleLogin = () => {
    console.log(userId);
    console.log(password);
    console.log(props.users);
    const userFound = users.find((item) => item.userId === userId);
    console.log(userFound);
    if (userFound) {
      if (userFound.password === password) {
        navigate("/pirate/clue1");
      }
    } else {
      navigate("/pirate/error");
    }
  };
  return (
    <div id="pirateHome">
      <div id="pirateDiv">
        Enter userId :{" "}
        <input
          onChange={(event) => setUserId(event.target.value)}
          type="text"
        />
        <br />
        Enter Password :{" "}
        <input
          onChange={(event) => setPassword(event.target.value)}
          type="password"
        />
        <br />
        {/* <button onClick={handleLogin}>Login</button> */}
        <button className="piratebtn success" onClick={handleLogin}>
          Login
        </button>
        <br />
        New User ? <br />
        <a href="/piratesignup"> SignUp</a>
      </div>
    </div>
  );
};

export default PirateLogin;
