import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Modal from "./ClueModal/Modal/Modal";
import Backdrop from "./ClueModal/Backdrop/Backdrop";

import "./Stage3.css";
const Stage3 = () => {
  const navigate = useNavigate();
  // const [answer3, setAnswer3] = useState("");
  const [hint3, setHint3] = useState(false);
  // const [wrong, setWrong] = useState(false);
  const [viewbutton, setViewbutton] = useState(false);
  //   const [ans , showAns] = useState(false);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const closeModal = () => {
    setModalIsOpen(false);
    setTimeout(() => {
      navigate("/pirate/stage4");
    }, 1000);
  };

  const handleHint3 = () => {
    setHint3(true);
  };

  setTimeout(() => {
    setViewbutton(true);
  }, 1000);

  return (
    <div id="stage3Div">
      <Modal show={modalIsOpen} closed={closeModal} data={"AD"} />
      <Backdrop />

      <p>Good.!! Let's give some task to brains.</p>
      <h1>
        Guess the shift and Decipher the ceaser te<b>X</b>t.
      </h1>
      <p>
        what is{" "}
        <a href="https://cryptii.com/pipes/caesar-cipher">ceaser cipher</a>
      </p>
      <i>G bmlr ilmu ufw zsr hyai'q fyr gq qsqngagmsq.</i>
      <p></p>
      <textarea id="textarea" placeholder="workspace"></textarea>
      <div id="sayingDiv1">
        <span>
          <a id="clickme">click me </a>
          <img className="move2" id="jackStage1Image" src="/images/pic2.webp" />
        </span>

        {hint3 && (
          <div className="move" id="thinkDiv1">
            <p id="dialogue">
              The only
              <br /> alphabet a <br />
              pirate uses.
              <br />
            </p>
          </div>
        )}
      </div>

      {viewbutton && (
        <button id="help1" onClick={handleHint3} className="piratebtn info">
          Help me Jack
        </button>
      )}
    </div>
  );
};

export default Stage3;
