import { useState } from "react";
import { useNavigate } from "react-router-dom";

import "./Stage3.css";
const Stage3 = () => {
  const navigate = useNavigate();
  // const [answer3, setAnswer3] = useState("");
  const [hint3, setHint3] = useState(false);
  // const [wrong, setWrong] = useState(false);
  const [viewbutton, setViewbutton] = useState(false);
  //   const [ans , showAns] = useState(false);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const closeModal = () => {
    setModalIsOpen(false);
    setTimeout(() => {
      navigate("/pirate/stage4");
    }, 1000);
  };

  return (
    <div id="stage3Div">
      <p>Good.!! Let's give some task to brains.</p>
      <h1>Decipher the text and you are free to go.</h1>
      <i>G bmlr ilmu ufw zsr hyai'q fyr gq qsqngagmsq.</i>
      <p></p>

      <div id="sayingDiv1">
        <span>
          <img className="move" id="jackStage1Image" src="/images/pic2.webp" />
        </span>

        {hint3 && (
          <div className="move" id="thinkDiv1">
            <p id="dialogue">
              The only alphabet a pirate uses.
              <br />
            </p>
          </div>
        )}
      </div>

      {viewbutton && (
        <button id="help1" onClick={handleHint3} className="piratebtn info">
          Help me Jack
        </button>
      )}
    </div>
  );
};

export default Stage3;
