import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { userActions } from "../store/user-slice";
import { useSelector } from "react-redux";
import Modal from "./ClueModal/Modal/Modal";
// import backdrop from "./ClueModal/Backdrop/Backdrop";
import Backdrop from "./ClueModal/Backdrop/Backdrop";
import "./Stage1.css";

const Stage1 = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const stage1Time = useSelector((state) => state.stage1Time);
  const [answer, setAnswer] = useState("");
  const [hint1, setHint1] = useState(false);
  const [wrong, setWrong] = useState(false);
  const [viewbutton, setViewbutton] = useState(false);
  //   const [ans , showAns] = useState(false);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  let startTime, endTime;
  useEffect(() => {
    startTime = new Date() * 1;
  }, []);

  const closeModal = () => {
    setModalIsOpen(false);
    setTimeout(() => {
      endTime = new Date() * 1;
      const elapsedTime = endTime - startTime;
      dispatch(userActions.setStage1Time(elapsedTime));
      navigate("/pirate/stage2");
    }, 1000);
  };
  const handleCheck = () => {
    if (answer) {
      if (answer.toLowerCase() === "treasure") {
        // navigate("/pirate/stage2");
        setModalIsOpen(true);
      } else {
        setWrong(true);
      }
    } else {
      setWrong(true);
    }
  };

  const handleHint = () => {
    setHint1(true);
  };

  setTimeout(() => {
    setViewbutton(true);
  }, 1000);

  return (
    <div id="stage1Div">
      <Modal show={modalIsOpen} closed={closeModal} data={"AM"} />
      <Backdrop />

      <p>
        <i>
          "I'm buried on an island, hidden from view. You'll need a map and a
          shovel too. What am I?"
        </i>
      </p>
      <p>Solve me to go further.</p>

      <input
        className="answerInput"
        type="text"
        onChange={(event) => setAnswer(event.target.value)}
      ></input>
      <button onClick={handleCheck} className="piratebtn default">
        Check
      </button>

      {wrong && <p id="wrongAnswer">Oops !! wrong answer.</p>}
      <div id="sayingDiv1">
        <span>
          <img className="move" id="jackStage1Image" src="/images/pic2.webp" />
        </span>

        {hint1 && (
          <div className="move" id="thinkDiv1">
            <p id="dialogue">
              Sounds like something
              <br /> valuable
            </p>
          </div>
        )}
      </div>

      {viewbutton && (
        <button id="help1" onClick={handleHint} className="piratebtn info">
          Help me Jack
        </button>
      )}
    </div>
  );
};

export default Stage1;
