import { createSlice } from "@reduxjs/toolkit";

const userSlice = createSlice({
  initialState: [
    {
      userId: "bharadwaj",
      password: "1234567",
      stage1: 0,
      stage2: 0,
      stage3: 0,
      stage4: 0,
      stage5: 0,
    },
  ],
  name: "user",
  reducers: {
    setUser(state, action) {
      state.push(action.payload);
    },
  },
});

export default userSlice;
export const userActions = userSlice.actions;
