import { createSlice } from "@reduxjs/toolkit";
import { current } from "@reduxjs/toolkit";
const userSlice = createSlice({
  initialState: [
    {
      index: 0,
      userId: "bharadwaj",
      password: "1234567",
      stage1Time: 0,
      stage2Time: 0,
      stage3Time: 0,
      stage4Time: 0,
      stage5Time: 0,
      stage1Points: 0,
      stage2Points: 0,
      stage3Points: 0,
      stage4Points: 0,
      stage5Points: 0,
    },
  ],
  name: "user",
  reducers: {
    setUser(state, action) {
      state.push(action.payload);
    },
    setStage1Time(state, action) {
      const foundUser = state.find(
        (item) => item.userId === action.payload.userId
      );
      console.log(current(state));
      // state[foundUser.index].stage1Time = action.payload.time;
      foundUser.stage1Time = action.payload.time;
      console.log(current(state));

      // const oldarray = [...state];
      // const newarray = [
      //   ...oldarray.slice(0, foundUser.index),
      //   ...[{ ...foundUser, stage1Time: action.payload.time }],
      //   ...oldarray.slice(foundUser.index + 1),
      // ];
      // return newarray;

      // console.log("inside setStage1 ,state is", state);
      // console.log("inside setStage1 ,action is", action);
      // console.log("foundUser", foundUser);
      // foundUser.stage1Time = action.payload.time;
      // console.log("stage1Time inside setStage1", foundUser.stage1Time);
    },
    setStage2Time(state, action) {
      state.stage2Time = action.payload;
    },
    setStage3Time(state, action) {
      state.stage3Time = action.payload;
    },
    setStage4Time(state, action) {
      state.stage4Time = action.payload;
    },
    setStage5Time(state, action) {
      state.stage5Time = action.payload;
    },

    setStage1Points(state, action) {
      state.stage1Points = action.payload;
    },
    setStage2Points(state, action) {
      state.stage2Points = action.payload;
    },
    setStage3Points(state, action) {
      state.stage3Points = action.payload;
    },
    setStage4Points(state, action) {
      state.stage4Points = action.payload;
    },
    setStage5Points(state, action) {
      state.stage5Points = action.payload;
    },
  },
});

export default userSlice;
export const userActions = userSlice.actions;
