import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Stage1.css";

const Stage1 = () => {
  const navigate = useNavigate();
  const [answer, setAnswer] = useState("");
  const [hint1, setHint1] = useState("...");
  const [wrong, setWrong] = useState(false);
  const [viewbutton, setViewbutton] = useState(false);
  const handleCheck = () => {
    if (answer === "treasure") {
      navigate("/pirate/stage2");
    } else {
      setWrong(true);
    }
  };
  const handleHint = () => {
    setHint1("Sounds like something valueable");
  };

  setTimeout(() => {
    setViewbutton(true);
  }, 10000);
  return (
    <div id="stage1Div">
      <p>
        "I'm buried on an island, hidden from view. You'll need a map and a
        shovel too. What am I?"
      </p>
      <p>Solve me to go further.</p>
      <input
        className="answerInput"
        type="text"
        onChange={(event) => setAnswer(event.target.value)}
      ></input>
      <button onClick={handleCheck} className="piratebtn default">
        Check
      </button>
      {wrong && <p id="wrongAnswer">Oops !! wrong answer.</p>}
      <div id="sayingDiv">
        <span>
          <img id="jackStage1Image" src="/images/pic2.webp" />
        </span>
        <div id="thinkDiv">
          <p id="dialogue">{hint1}</p>
        </div>
      </div>
      {viewbutton && (
        <button id="help" onClick={handleHint} className="piratebtn info">
          Help me Jack
        </button>
      )}
    </div>
  );
};

export default Stage1;
